import React, { useState } from "react";

import TotalDisplay from "./components/TotalDisplay";
import CalcButton from "./components/CalcButton";
import { useReducer } from "react";
import { initialState } from "./reducers";
import reducer from "./reducers";

import { addOne, ADD_ONE, applyNumber, APPLY_NUMBER ,CHANGE_OPERATION,} from "./actions";

// const [calculatorParams, setParams] = useState(initialState)

function App() {
  const [state, dispatch] = useReducer(reducer, initialState);

  function handleNumber(e) {
    dispatch(applyNumber(Number(e.target.value)));
  }

  function handleNumber2(e) {
    dispatch({ type: APPLY_NUMBER, payload: e.target.value });
  }

  return (
    <div className="App">
      <nav className="navbar navbar-dark bg-dark">
        <span className="navbar-brand"> Reducer Challenge</span>
      </nav>

      <div className="container row mt-5">
        <div className="col-md-12 d-flex justify-content-center">
          <form name="Cal">
            <TotalDisplay value={state.total} />
            <div className="row details">
              <span id="operation">
                <b>Operation:</b> {state.operation}
              </span>
              <span id="memory">
                <b>Memory:</b> {state.memory}
              </span>
            </div>

            <div className="row">
              <CalcButton value={"M+"} />
              <CalcButton value={"MR"} />
              <CalcButton value={"MC"} />
            </div>

            <div className="row">
              <CalcButton
                onClick={() => {
                  // dispatch(addOne());
                  dispatch({ type: ADD_ONE });
                }}
                value={1}
              />
              <CalcButton value={2} onClick={(e) => handleNumber(e)} />
              <CalcButton
                value={3}
                onClick={() => {
                  dispatch({ type: APPLY_NUMBER, payload: 3 });
                }}
              />
            </div>

            <div className="row">
              <CalcButton
                value={4}
                onClick={(e) => {
                  dispatch(applyNumber(Number(e.target.value)));
                }}
              />
              <CalcButton onClick={(e) => handleNumber(e)} value={5} />
              <CalcButton onClick={(e) => handleNumber(e)} value={6} />
            </div>

            <div className="row">
              <CalcButton onClick={(e) => handleNumber(e)} value={7} />
              <CalcButton onClick={(e) => handleNumber(e)} value={8} />
              <CalcButton onClick={(e) => handleNumber(e)} value={9} />
            </div>

            <div className="row">
              <CalcButton value={"+"}
                onClick={() =>
                  dispatch({ type: CHANGE_OPERATION, payload: "+" })
                } />
              <CalcButton value={"*"} onClick={() =>
                  dispatch({ type: CHANGE_OPERATION, payload: "*" })
                } />
              <CalcButton value={"-"} onClick={() =>
                  dispatch({ type: CHANGE_OPERATION, payload: "-" })
                } />
            </div>

            <div className="row ce_button">
              <CalcButton value={"CE"} />
            </div>
          </form>
        </div>
      </div>
    </div>
  );
}

export default App;
